import React, { Component } from 'react'
import { DragSource } from 'react-dnd';

const cardSource = {
    beginDrag(props) {
      return {
        text: props.text
      };
    }
  };

  /**
   * Specifies the props to inject into your component.
   */
  function collect(connect, monitor) {
    return {
      connectDragSource: connect.dragSource(),
      isDragging: monitor.isDragging()
    };
  }


class Item extends Component {
    constructor(){
        super()
        this.state = {
        }
    }

    render () {
        const { listId, selected, onClick, connectDragSource } = this.props
        return connectDragSource(
            <li  onClick={() => onClick(listId)} className={selected.includes(listId) ? "selected" : "unselected"}>
                <img alt="foo" src="https://place-hold.it/256x256" />
            </li>
        )
    }
}

export default DragSource( cardSource, collect)(Item)
