import React, { Component } from 'react'
import Item from './Item'
import { collisionDetection } from './helper.js'


class Items extends Component {
    constructor(){
        super()
        this.state = {
            selected: []
        }
    }

    static getDerivedStateFromProps(props, state){
        if(props.visibility === "hidden"){
            return null
        }
        const elements = document.getElementsByClassName('unselected')
        for (let i = 0; i < elements.length; i++) { 
            let selected = []
            const element = elements[i]
            const collisionDetected = collisionDetection(props.mousePosition, element.getBoundingClientRect())
            if(collisionDetected){ 
                const listId = parseInt(element.getAttribute('id'))
                selected.push(listId)
            }
            return {selected: selected}
        }
    }

    mousedown = (listId) => {
        this.setState({selected: [listId]})
    }

    render () {
        console.log(this.state)
        const { mousePosition } = this.props
        return (
            <React.Fragment>
                <Item mousePosition={mousePosition} key={1} listId={1} selected={this.state.selected} MouseDown={this.mousedown}/>
                <Item mousePosition={mousePosition} key={2} listId={2} selected={this.state.selected} MouseDown={this.mousedown}/>
                <Item mousePosition={mousePosition} key={3} listId={3} selected={this.state.selected} MouseDown={this.mousedown}/>
                <Item mousePosition={mousePosition} key={4} listId={4} selected={this.state.selected} MouseDown={this.mousedown}/>
                <Item mousePosition={mousePosition} key={5} listId={5} selected={this.state.selected} MouseDown={this.mousedown}/>
                <Item mousePosition={mousePosition} key={6} listId={6} selected={this.state.selected} MouseDown={this.mousedown}/>
                <Item mousePosition={mousePosition} key={7} listId={7} selected={this.state.selected} MouseDown={this.mousedown}/>

           </React.Fragment>
        )
    }
}

export default Items
