import React, { Component } from 'react'
import Item from './Item'
import { collisionDetection } from './helper.js'


class Items extends Component {
    constructor(){
        super()
        this.state = {
            selected: []
        }
    }

    static getDerivedStateFromProps(props, state){
        const boxCoordinates = reCalc(props.mousePosition)
        const elements = document.getElementsByClassName('unselected')
        for (var i = 0; i < elements.length; i++) {

            console.log(elements[i].getBoundingClientRect()); //second console output
        }
    }


    mousedown = (listId) => {
        this.setState({selected: [listId]})
    }

    checkCollision = () => {
        for (var i = 0; i < elements.length; i++) {
            console.log(elements[i].getBoundingClientRect()); //second console output
        }
        //this.setState({selected: [...this.state.selected, listId]})
    }

    render () {
        const { mousePosition } = this.props
        return (
            <React.Fragment>
                <Item mousePosition={mousePosition} key={1} listId={1} selected={this.state.selected} MouseDown={this.mousedown}/>
                <Item mousePosition={mousePosition} key={2} listId={2} selected={this.state.selected} MouseDown={this.mousedown}/>
           </React.Fragment>
        )
    }
}

export default Items
