import React, { Component } from 'react';
import { DragSource } from 'react-dnd';
import { getOffsetToMouse } from './helper.js'

const itemSource = {
    beginDrag(props) {
      return {
        listId: props.listId,
        selectedItems: props.selectedItems
      };
    },
    endDrag(props, monitor, component) {
        if (!monitor.didDrop()) {
          return;
        }
        const item = monitor.getItem();
        console.log(item)
    }
};

function collect(connect, monitor) {
    return {
        connectDragPreview: connect.dragPreview(),
        connectDragSource: connect.dragSource(),
        isDragging: monitor.isDragging()
    };
}

class Item extends Component { 

    constructor(){
        super()
        this.draggingStyle={
            // WebkitTransform: "translate(3em,0)",
            // MozTransform: "translate(3em,0)",
            // OTransform: "translate(3em,0)",
            // MsTransform: "translate(3em,0)",
            transform: "translate(3em,0)"
        }
    }
    render () {
        const { listId, selectedItems, onmousedown, connectDragSource, isDragging, mousePosition} = this.props

        if(isDragging){
            getOffsetToMouse(listId, thi)
        }

        return connectDragSource(
            <li key={"li" + listId} type={"selectableElement"} listid={listId} onMouseDown={() => onmousedown(listId)} className={selectedItems.includes(listId) ? "selected" : "unselected"}>
                <img key={"img" + listId} type={"selectableElement"} alt="foo" src="https://place-hold.it/256x256" />
                <img key={"img" + listId + "dragging"} type={"selectableElement"} alt="foo" src="https://place-hold.it/256x256" style={isDragging ? this.draggingStyle : null}/>
            </li>
        )
    }
}

export default DragSource("Item", itemSource, collect)(Item)
