import React, { Component } from 'react'
import { DragSource } from 'react-dnd';
import { collisionDetection } from './helper.js'

const itemSource = {
    beginDrag(props) {
      return {
        listId: props.listId
      };
    },
    endDrag(props, monitor, component) {
        if (!monitor.didDrop()) {
          return;
        }
        const item = monitor.getItem();
        console.log(item)
    }
};

function collect(connect, monitor) {
    return {
        connectDragSource: connect.dragSource(),
        isDragging: monitor.isDragging()
    };
}

class Item extends Component {
    constructor(){
        super()
        this.state = {
        }
    }

    componentDidMount() {
        const elements = document.getElementsByClassName('selected')
        for (var i = 0; i < elements.length; i++) {
            console.log(elements[i].getBoundingClientRect()); //second console output
        }
    }

    render () {
        const { listId, selected, MouseDown, connectDragSource } = this.props
        return connectDragSource(
            <li className="item" id={listId} onMouseDown={() => MouseDown(listId)} className={selected.includes(listId) ? "selected" : "unselected"}>
                <img alt="foo" src="https://place-hold.it/256x256" />
            </li>
        )
    }
}

export default DragSource("Item", itemSource, collect)(Item)
