import React, { Component } from 'react'
import Item from './Item'

class Items extends Component {
    constructor(){
        super()
        this.state = {
            selected: [2]
        }
    }

    onClick = (lisId) => {
        if (selected.length === 0){
            this.setState({selected: [...this.state.selected, lisId]})
        }
    }

    render () {

        return (
            <React.Fragment>
                <Item key={1} listId={1} selected={this.state.selected} onClick={this.onClick}/>
                <Item key={2} listId={2} selected={this.state.selected} onClick={this.onClick}/>
           </React.Fragment>
        )
    }
}

export default Items
