import React, { Component } from 'react'
import { DragSource } from 'react-dnd';

const itemSource = {
    beginDrag(props) {
      return {
        listId: props.listId
      };
    },
    endDrag(props, monitor, component) {
        if (!monitor.didDrop()) {
          return;
        }
        const item = monitor.getItem();
        console.log(item)
    }
};

function collect(connect, monitor) {
    return {
        connectDragPreview: connect.dragPreview(),
        connectDragSource: connect.dragSource(),
        isDragging: monitor.isDragging()
    };
}

class Item extends Component {
    componentDidMount() {
        const img = new Image();
        img.src = "https://www.google.com/url?sa=i&source=images&cd=&cad=rja&uact=8&ved=2ahUKEwini_v4_qHeAhUCZN8KHQjzDcwQjRx6BAgBEAU&url=http%3A%2F%2Fcallofduty.wikia.com%2Fwiki%2FFile%3AHoff_The_Charts!_trophy_icon_CoDIW.png&psig=AOvVaw20OrfnIFSApNwZyKY-Ffl2&ust=1540570626350650"
        img.onload = () => this.props.connectDragPreview(img);
      }
    render () {
        const { listId, selectedItems, onmousedown, connectDragSource } = this.props
        return connectDragSource(
            <li type={"selectableElement"} listid={listId} onMouseDown={() => onmousedown(listId)} className={selectedItems.includes(listId) ? "selected" : "unselected"}>
                <img type={"selectableElement"} alt="foo" src="https://place-hold.it/256x256" />
            </li>
        )
    }
}

export default DragSource("Item", itemSource, collect)(Item)
