import React, { Component } from 'react'

class Item extends Component {
    constructor(){
        super()
        this.state = {
            selected: false
        }
    }

    onClick = () => {
        if(this.props.selected){
            return
        }
        this.setState({selected: !this.state.selected})

    }

    render () {
        return (
            <li onClick={this.props.onClick} class={this.state.selected ? "selected" : "unselected"}>
                <img alt="foo" src="https://place-hold.it/256x256" />
            </li>
        )
    }
}

export default Item
