import React, { Component } from 'react';
import { DragSource } from 'react-dnd';
import { getOffsetToMouse } from './helper.js'
import { getEmptyImage } from 'react-dnd-html5-backend'


const itemSource = {
    beginDrag(props) {
      // Set global dragging state to true
      props.ondrag(true)
      return {
        listId: props.listId,
        selectedItems: props.selectedItems
      };
    },
    endDrag(props, monitor, component) {
        // Set global dragging state to false
        props.ondrag(false)  
        if (!monitor.didDrop()) {
          return;
        }
        const item = monitor.getItem();
        console.log(item)
    },
    isDragging(props, monitor){
    }

};

function collect(connect, monitor) {
    return {
        connectDragSource: connect.dragSource(),
        isDragging: monitor.isDragging(),
        connectDragPreview: connect.dragPreview()
    };
}

class Item extends Component {  
    constructor(){
        super()
        this.rotate = Math.random() * 10 - 20 // 5
        this.transform = "transform 0.1s ease 0s"   
    }

    shouldComponentUpdate(nextProps, nextState) {
        if(nextProps.selectedItems.includes(this.props.listId) !== this.props.selectedItems.includes(this.props.listId)){
            return true
        }
        else if(nextProps.selectedItems.includes(this.props.listId) && this.props.somethingIsDragging){
            return true
        }
        return false
    }

    componentDidMount() {
        this.props.connectDragPreview(getEmptyImage(), {
            captureDraggingState: false,
        })
    }

    render () {
        const { listId, selectedItems, onmousedown, connectDragSource, mousePosition, somethingIsDragging} = this.props
        let dragStyle = null
        if(selectedItems.includes(listId) && somethingIsDragging){
            const offset = getOffsetToMouse(listId, mousePosition)
            const translate = "translate(" + offset.x + "px, " + offset.y + "px) rotate(" + this.rotate + "deg)"
            dragStyle = {zIndex: 2000, transform: translate, transition: this.transform}
        }
        return connectDragSource(
            <div name={"selectableElement"} key={"li" + listId} type={"selectableElement"} listid={listId} onMouseDown={() => onmousedown(listId)} className={selectedItems.includes(listId) ? "selected" : "unselected"} onDrag={ondrag}>
                <img key={"img" + listId} type={"selectableElement"} alt="foo" src="https://place-hold.it/256x256" style={{position: "absolute", zIndex: 1}}/>
                <img key={"img" + listId + "dragging"} id={"dragPreview" + String(listId)} alt="foo" src="https://place-hold.it/256x256" style={dragStyle}/>
            </div>
        )
    }
}

export default DragSource("Item", itemSource, collect)(Item)
