import React, { Component } from 'react';
import './Gallery.css';
import Items from './Items.js'
import Target from './Target.js'
import Selectionbox from './Selectionbox.js'
import HTML5Backend from 'react-dnd-html5-backend'
import { DragDropContext } from 'react-dnd'


class Gallery extends Component {
    constructor(){
      super()
      this.state = {
          selected: [],
          mousePosition: {
              x1: 0,
              x2: 0,
              y1: 0,
              y2: 0,
          },
          visibility: "hidden",
      }
    }

    onmousedown = (e) => {
        this.setState({visibility: "visible"})
        let currentMousePosition = this.state.mousePosition
        currentMousePosition.x1 = e.clientX; //Set the initial X
        currentMousePosition.y1 = e.clientY; //Set the initial Y
        this.setState({mousePosition: currentMousePosition})
    };

    onmousemove = (e) => {
      let currentMousePosition = this.state.mousePosition
      currentMousePosition.x2 = e.clientX; 
      currentMousePosition.y2 = e.clientY;
      this.setState({mousePosition: currentMousePosition})
    };

    onmouseup = (e) => {
        this.setState({visibility: "hidden"})
    };

    selectItem = (listId) => {
        this.setState({selected: [listId]})
    }

    render() {
        return (
            <div className="container" onMouseDown={this.onmousedown} onMouseMove={this.onmousemove} onMouseUp={this.onmouseup}>
                  <Target/>
                <ul>
                  <Items mousePosition={this.state.mousePosition} visibility={this.state.visibility} selectionboxCoordinates={this.state.selectionboxCoordinates}/>
                </ul>
            </div>
        );
    }
}

export default DragDropContext(HTML5Backend)(Gallery);
