import React, { Component } from 'react';
import { DragSource } from 'react-dnd';
import { getOffsetToMouse } from './helper.js'

const itemSource = {
    beginDrag(props) {
      return {
        listId: props.listId,
        selectedItems: props.selectedItems
      };
    },
    endDrag(props, monitor, component) {
        if (!monitor.didDrop()) {
          return;
        }
        const item = monitor.getItem();
        console.log(item)
    }
};

function collect(connect, monitor) {
    return {
        connectDragSource: connect.dragSource(),
        isDragging: monitor.isDragging(),
        connectDragPreview: connect.dragPreview()
    };
}

class Item extends Component {  
    constructor(){
        super()
        this.rotate = Math.random() * 10 - 20 // 5

    }
    componentDidMount() {
        const element = <div style={{visibility: "hidden"}}></div>
        this.props.connectDragPreview(element);
      }

    render () {
        const { listId, selectedItems, onmousedown, connectDragSource, isDragging, mousePosition} = this.props
        let dragStyle = null
        if(selectedItems.includes(listId)){
            const offset = getOffsetToMouse(listId, mousePosition)
            dragStyle = {zIndex: listId, transform: "translate(" + offset.x + "px, " + offset.y + "px) rotate(" + this.rotate + "deg)", transition: "transform 0.1s ease 0s"}
        }
        return connectDragSource(
            <li key={"li" + listId} type={"selectableElement"} listid={listId} onMouseDown={() => onmousedown(listId)} className={selectedItems.includes(listId) ? "selected" : "unselected"}>
                <img key={"img" + listId} type={"selectableElement"} alt="foo" src="https://place-hold.it/256x256" style={{position: "absolute", zIndex: 1}}/>
                <img key={"img" + listId + "dragging"} id={"dragPreview" + String(listId)} alt="foo" src="https://place-hold.it/256x256" style={dragStyle}/>
            </li>
        )
    }
}

export default DragSource("Item", itemSource, collect)(Item)
