import React, { Component } from 'react';
import './Gallery.css';
import Items from './Items.js'
import Target from './Target.js'
import HTML5Backend from 'react-dnd-html5-backend'
import { DragDropContext } from 'react-dnd'


function reCalc(div, x1, x2, y1, y2) { //This will restyle the div
    var x3 = Math.min(x1,x2); //Smaller X
    var x4 = Math.max(x1,x2); //Larger X
    var y3 = Math.min(y1,y2); //Smaller Y
    var y4 = Math.max(y1,y2); //Larger Y
    div.style.left = x3 + 'px';
    div.style.top = y3 + 'px';
    div.style.width = x4 - x3 + 'px';
    div.style.height = y4 - y3 + 'px';
}

class Gallery extends Component {

    constructor(){
      super()
      this.div = null
    }

    componentDidMount(){
      this.div = document.getElementById('div')
      this.div.style.width = '300px'
      this.div.style.height = '300px'
      this.div.style.backgroundColor ='red'

    }
    onmousedown = function(e) {
        this.div.hidden = 0; //Unhide the div
        let x1 = e.clientX; //Set the initial X
        let y1 = e.clientY; //Set the initial Y
        reCalc();
    };

    onmousemove = function(e) {
       // let x2 = e.clientX; //Update the current position X
       // let y2 = e.clientY; //Update the current position Y
       // reCalc();
    };

    onmouseup = function(e) {
        this.div.hidden = 1; //Hide the div
    };

    render() {
        return (
            <div onMouseDown={this.onmousedown} onmousemove={this.onmousemove} onmouseup={this.onmouseup}>
                  <Target/>
                <ul>
                  <div id="div" hidden></div>
                  <Items/>
                </ul>
            </div>
        );
    }
}

export default DragDropContext(HTML5Backend)(Gallery);
