import React, { Component } from 'react';
import './Gallery.css';
import Items from './Items.js'
import Target from './Target.js'
import Selectionbox from './Selectionbox.js'
import HTML5Backend from 'react-dnd-html5-backend'
import { DragDropContext } from 'react-dnd'



class Gallery extends Component {
    constructor(){
      super()
      this.state = {
          mousePosition: {
              x1: 0,
              x2: 0,
              y1: 0,
              y2: 0,
          }
      }
      this.mousePosition = {

      }
    }

    reCalc() { 
        //This will restyle the div
        let x3 = Math.min(this.mousePosition.x1, this.mousePosition.x2); //Smaller X
        let x4 = Math.max(this.mousePosition.x1, this.mousePosition.x2); //Larger X
        let y3 = Math.min(this.mousePosition.y1, this.mousePosition.y2); //Smaller Y
        let y4 = Math.max(this.mousePosition.y1, this.mousePosition.y2); //Larger Y
        let left = x3 + 'px';
        let top = y3 + 'px';
        let width = x4 - x3 + 'px';
        let height = y4 - y3 + 'px';
        this.setState({left: left, top: top, width: width, height: height})
    }

    onmousedown = function(e) {
        console.log(true)
        //this.div.hidden = 0; //Unhide the div
        this.mousePosition.x1 = e.clientX; //Set the initial X
        this.mousePosition.y1 = e.clientY; //Set the initial Y
        this.reCalc();
    };

    onmousemove = function(e) {
      this.mousePosition.x2 = e.clientX; //Update the current position X
      this.mousePosition.y2 = e.clientY; //Update the current position Y
      this.reCalc();
    };

    onmouseup = function(e) {
        this.div.hidden = 1; //Hide the div
    };

    render() {
        return (
            <div>
                  <Selectionbox/>
                  <Target/>
                <ul>
                  <Items/>
                </ul>
            </div>
        );
    }
}

export default DragDropContext(HTML5Backend)(Gallery);
