import React from 'react'
import Item from './Item'
import ReactDOM from 'react-dom';
import { Grid } from 'react-virtualized';


const Items = (props) => {
    const onmousedown = (listId) => {
        props.selectItem(listId)
    }

  let count = 0
ondrag = function (e) {
  this.console.log(e)
  return {x: e.screenX, y: e.screenY}
   // static getDerivedStateFromProps(props, state){
  //   props.setMousePosition(props.screenOffset)
  //   return state

  // }
}


  const cellRenderer = function ({ columnIndex, key, rowIndex, style }) {
    let newStyle ={...style, margin: "32px"}
    count = count + 1
    return (
      <div key={key} style={newStyle} onDrag={ondrag}>  
          <Item
              key={key} 
              listId={key} 
              selectedItems={props.selectedItems} 
              onmousedown={onmousedown} 
              mousePosition={props.mousePosition} 
              ondrag={props.ondrag}
              somethingIsDragging={props.somethingIsDragging}
          />
      </div>
    )  
  }

    return (
        <React.Fragment>
            <Grid
                cellRenderer={cellRenderer}
                columnCount={20}
                columnCount={5}
                columnWidth={300}
                height={600}
                rowCount={100}
                rowHeight={300}
                width={1500}
                style={{zIndex: 1}}
              />
        </React.Fragment>
    )
}

export default Items
