import React, { Component } from 'react';
import './Gallery.css';

class Selectionbox extends Component {
    constructor(){
        super()
        this.state = {
            style: {
                hidden: 0,
                left: '0px',
                top: '0px',
                width: '0px',
                height: '0px'
            }
        }
        this.mousePosition = {
            x1: 0,
            x2: 0,
            y1: 0,
            y2: 0,
        }
    }

    reCalc() { 
        //This will restyle the div
        const x3 = Math.min(this.mousePosition.x1, this.mousePosition.x2); //Smaller X
        const x4 = Math.max(this.mousePosition.x1, this.mousePosition.x2); //Larger X
        const y3 = Math.min(this.mousePosition.y1, this.mousePosition.y2); //Smaller Y
        const y4 = Math.max(this.mousePosition.y1, this.mousePosition.y2); //Larger Y
        const left = x3 + 'px';
        const top = y3 + 'px';
        const width = x4 - x3 + 'px';
        const height = y4 - y3 + 'px';
        this.setState({left: left, top: top, width: width, height: height})
    }

    onmousedown = function(e) {
        //this.div.hidden = 0; //Unhide the div
        this.mousePosition.x1 = e.clientX; //Set the initial X
        this.mousePosition.y1 = e.clientY; //Set the initial Y
        this.reCalc();
    };

    onmousemove = function(e) {
       this.mousePosition.x2 = e.clientX; //Update the current position X
       this.mousePosition.y2 = e.clientY; //Update the current position Y
       this.reCalc();
    };

    onmouseup = function(e) {
        this.div.hidden = 1; //Hide the div
    };

    render() {
        return (
            <div style={this.state.style} onMouseDown={this.onmousedown} onmousemove={this.onmousemove} onmouseup={this.onmouseup}/>
        );
    }
}

export default Selectionbox;
