import React, { Component } from 'react';
import { DragSource } from 'react-dnd';
import { getOffsetToMouse } from './helper.js'

const itemSource = {
    beginDrag(props) {
      return {
        listId: props.listId,
        selectedItems: props.selectedItems
      };
    },
    endDrag(props, monitor, component) {
        if (!monitor.didDrop()) {
          return;
        }
        const item = monitor.getItem();
        console.log(item)
    }
};

function collect(connect, monitor) {
    return {
        connectDragPreview: connect.dragPreview(),
        connectDragSource: connect.dragSource(),
        isDragging: monitor.isDragging()
    };
}

class Item extends Component { 

    constructor(){
        super()
        this.state ={
            prevMousePosition: {},
            offsetToMouse: {},
            style:{
                transform: "translate3d(0,0)"
            }
        }
    }

    static getDerivedStateFromProps(props, state){
        if(state.prevMousePosition !== props.mousePosition){
            return {
                style: "translate3d(0,0)",
                prevMousePosition: props.mousePosition
            }
        }
        else{
            return null
        } 
    }

    componentDidMount(){
        const offsetToMouse = getOffsetToMouse(this.props.listId, this.props.mousePosition)
        this.setState({offsetToMouse: offsetToMouse})
    }


    render () {
        const { listId, selectedItems, onmousedown, connectDragSource, isDragging, mousePosition} = this.props
        console.log(this.state.offsetToMouse)
        // if(isDragging){
        //     getOffsetToMouse(listId, mousePosition)
        // }

        return connectDragSource(
            <li key={"li" + listId} type={"selectableElement"} listid={listId} onMouseDown={() => onmousedown(listId)} className={selectedItems.includes(listId) ? "selected" : "unselected"}>
                <img key={"img" + listId} type={"selectableElement"} alt="foo" src="https://place-hold.it/256x256" />
                <img key={"img" + listId + "dragging"} id={"dragPreview" + String(listId)} ype={"selectableElement"} alt="foo" src="https://place-hold.it/256x256" style={isDragging ? this.state.syle : null}/>
            </li>
        )
    }
}

export default DragSource("Item", itemSource, collect)(Item)
