import React from 'react'
import Item from './Item'
import { Grid,  AutoSizer } from 'react-virtualized';


const Items = (props) => {
    const onmousedown = (listId) => {
        props.selectItem(listId)
    }

    let counter = 0
    const picturesPerRow = 3

    const quotient = Math.floor(props.images.length/picturesPerRow);
    const remainder = props.images.length % picturesPerRow;

    let rowCount = quotient

    if( remainder > 0 ){
        rowCount = rowCount + 1
    }


    const cellRenderer = function ({ columnIndex, key, rowIndex, style }) {

        counter = counter + 1
        // Cell renderer for virtualized list
        let newStyle ={...style}
        if(rowIndex === rowCount-1 && columnIndex > remainder-1){
            return
        }
        return (
        <div key={key} style={newStyle}>  
            <Item
                containerStyle={style}
                key={key} 
                listId={key} 
                selectedItems={props.selectedItems} 
                onmousedown={onmousedown} 
                ondrag={props.ondrag}
            />
        </div>
        )  
    }

  return (
        <AutoSizer>
            {({ height, width }) => (
                <Grid
                    cellRenderer={cellRenderer}
                    columnCount={picturesPerRow}
                    columnWidth={width/picturesPerRow}
                    height={height} /* This needs to be replaced dynamically in future */
                    rowCount={rowCount}
                    rowHeight={300}
                    width={width} /* This needs to be replaced dynamically in future */
                    style={{zIndex: 1, outline: "none"}}
                />
            )}
        </AutoSizer>
  )
}

export default Items
