import React, { Component } from 'react';
import './Gallery.css';
import { reCalc } from './helper.js'


class Selectionbox extends Component {
    constructor(){
        super()
        this.state = {
            style: {
                zIndex: 9000,
                position: "absolute",
                background: "#eaeaea",
                opacity: 0.2
            }
        }
    }

    static getDerivedStateFromProps(props, state){
        const boxCoordinates = reCalc(props.mousePosition)
        let style = {...state.style}
        style = {...style, ...boxCoordinates, visibility: props.visibility}
        return({style: style})
    }

    render() {
        return (
            <div style={this.state.style}/>
        );
    }
}

export default Selectionbox;
