import React, { Component } from 'react'
import Item from './Item'




class Items extends Component {
    constructor(){
        super()
        this.state = {
            selected: [1]
        }
    }

    MouseDown = (listId) => {
        this.setState({selected: [listId]})
    }

    render () {
        return (
            <React.Fragment>
                <Item key={1} listId={1} selected={this.state.selected} MouseDown={this.MouseDown}/>
                <Item key={2} listId={2} selected={this.state.selected} MouseDown={this.MouseDown}/>
           </React.Fragment>
        )
    }
}

export default Items
