import React, { Component } from 'react'
import Item from './Item'

class Items extends Component {
    constructor(){
        super()
        this.state = {
            selected: {}
        }
    }

    onClick = () => {
        this.setState({selected: !this.state.selected})
    }

    render () {
        const items = []
        for (let i = 0; i < 3; i++) {
            items[i] = <Item key={i} selected={this.state.selected} onClick={this.onClick}/>
            let state = {...this.state.selected, i: false};
            this.setState({selected: state})
        }   
        console.log(this.state.selected)

        return (
            <React.Fragment>
                {items}
           </React.Fragment>
        )
    }
}

export default Items
