import React from 'react'
import Item from './Item'
import { Grid } from 'react-virtualized';


const Items = (props) => {
    const onmousedown = (listId) => {
        props.selectItem(listId)
    }

  const cellRenderer = function ({ columnIndex, key, rowIndex, style }) {
    // Cell renderer for virtualized list
    let newStyle ={...style, margin: "32px"}
    return (
      <div key={key} style={newStyle}>  
          <Item
              key={key} 
              listId={key} 
              selectedItems={props.selectedItems} 
              onmousedown={onmousedown} 
              mousePosition={props.mousePosition} 
              ondrag={props.ondrag}
              somethingIsDragging={props.somethingIsDragging}
          />
      </div>
    )  
  }

  return (
      <React.Fragment>
          <Grid
              cellRenderer={cellRenderer}
              columnCount={5}
              columnWidth={300}
              height={600} /* This needs to be replaced dynamically in future */
              rowCount={10000}
              rowHeight={300}
              width={1500} /* This needs to be replaced dynamically in future */
              style={{zIndex: 1, outline: "none"}}
            />
      </React.Fragment>
  )
}

export default Items
