import React, { Component } from 'react';
import './Gallery.css';
import Items from './Items.js'
import Target from './Target.js'
import Selectionbox from './Selectionbox.js'
import HTML5Backend from 'react-dnd-html5-backend'
import { DragDropContext } from 'react-dnd'
import { collisionDetection } from './helper.js'



class Gallery extends Component {
    constructor(){
      super()
      this.state = {
          selected: [],
          mousePosition: {
              x1: 0,
              x2: 0,
              y1: 0,
              y2: 0,
          },
          visibility: "hidden",
      }
    }

    onmousedown = (e) => {
        // Only activate selection box when not dragging on a selectable item
        if(e.target.getAttribute("type") !== "selectableElement"){
            this.setState({visibility: "visible"})
            let currentMousePosition = this.state.mousePosition
            currentMousePosition.x1 = e.clientX; //Set the initial X
            currentMousePosition.y1 = e.clientY; //Set the initial Y
            this.setState({mousePosition: currentMousePosition})
        }
    };

    onmousemove = (e) => {
        // Always update mouse position
        let currentMousePosition = this.state.mousePosition
        currentMousePosition.x2 = e.clientX; 
        currentMousePosition.y2 = e.clientY; 
        this.setState({mousePosition: currentMousePosition})
        // Only check for collisions if selection box is active
        if(this.state.visibility === "visible"){
            const collisions = collisionDetection(currentMousePosition)
            this.setState({selected: collisions})
        }
    };

    onmouseup = (e) => {
        this.setState({visibility: "hidden"})
    }

    selectItem = (listId) => {
        // Set selected items
        this.setState({selected: [listId]})
    }

    onclick = (e) => {
        // Select single item by clicking on it
        console.log(this.state.selected)
        if(e.target.getAttribute("type") !== "selectableElement"){
            this.setState({selected: []})
        }
        this.setState({visibility: "hidden"})


    }

    render() {
        return (
            <div type="container" onMouseDown={this.onmousedown} onMouseMove={this.onmousemove} onMouseUp={this.onmouseup} onClick={this.onclick}>
                  <Selectionbox mousePosition={this.state.mousePosition} visibility={this.state.visibility} />
                  <Target/>
                <ul>
                  <Items selectItem={this.selectItem} selectedItems={this.state.selected}/>
                </ul>
            </div>
        );
    }
}

export default DragDropContext(HTML5Backend)(Gallery);
