import React, { Component } from 'react';
import './Gallery.css';


function reCalc(mousePosition) { 
    //This will restyle the div
    let doc = document.documentElement;
    let scrollOffsetY = (window.pageYOffset || doc.scrollTop)  - (doc.clientTop || 0); 
    let x3 = Math.min(mousePosition.x1, mousePosition.x2); //Smaller X
    let x4 = Math.max(mousePosition.x1, mousePosition.x2); //Larger X
    let y3 = Math.min(mousePosition.y1, mousePosition.y2); //Smaller Y
    let y4 = Math.max(mousePosition.y1, mousePosition.y2); //Larger Y
    let left = x3 + 'px';
    let top = y3 + scrollOffsetY + 'px';
    let width = x4 - x3 + 'px';
    let height = y4 - y3 + 'px';
    return {left: left, top: top, width: width, height: height}
}

class Selectionbox extends Component {
    constructor(){
        super()
        this.state = {
            style: {
                zIndex: 9000,
                position: "absolute",
                visibility: "hidden",
                left: '0px',
                top: '0px',
                width: '0px',
                height: '0px',
                backgroundColor: 'blue'
            }
        }
    }

    static getDerivedStateFromProps(props, state){
        const values = reCalc(props.mousePosition)
        let style = {...state.style}
        style.visibility = props.visibility
        style.left = values.left
        style.top = values.top
        style.width = values.width
        style.height = values.height
        if(style != state.style){
            props.passSelectionboxCoordinates(values)
            return ({ style: style})
        }
    }


    render() {
        return (
            <div id={"Selectionbox"} style={this.state.style}/>
        );
    }
}

export default Selectionbox;
